mapper.select:
SELECT rjc.title as workName,cc.`name` as city,rjb.payStart as startMoney,rjb.payEnd as endMoney,rjb.workYear as workYear ,ccy.authVip as rank,ccy.fullName as companyName,cu.tel as phone,cu.loginTime as lastLoginTime,rjb.createTime as releaseTime ,rjb.recommendNum as pushCount,rjb.classId as classId,rjb.companyId as companyId FROM recruit_job_base rjb LEFT JOIN recruit_job_extend rje on rje.jobId = rjb.id LEFT JOIN recruit_job_class rjc on rjc.id = rjb.classId LEFT JOIN common_business cb on cb.id = rjb.businessId LEFT JOIN common_city cc on cc.id = rjb.workCity LEFT JOIN common_company ccy on ccy.id=rjb.companyId LEFT JOIN common_user cu on cu.id=rjb.userId WHERE rjb.isExpress=0 AND rjb.isDelete=0
AND rjb.classId=#{classId}
AND rjb.workCity=#{workCity}
AND rjb.workYear=#{workYear}
AND ccy.authVip=#{rank}
AND (ccy.fullName LIKE CONCAT('%', #{keys}, '%') OR cu.tel LIKE CONCAT('%', #{keys}, '%'))
ORDER BY cu.loginTime DESC
ORDER BY cu.loginTime DESC
ORDER BY rjb.createTime DESC
limit #{curr},15

  SELECT COUNT(rjb.id)
  FROM recruit_job_base rjb
LEFT JOIN common_company ccy on ccy.id=rjb.companyId
LEFT JOIN  common_user cu on cu.id=rjb.userId
WHERE rjb.isExpress=0 AND  rjb.isDelete=0

  AND rjb.classId=#{classId}


  AND rjb.workCity=#{workCity}


  AND rjb.workYear=#{workYear}


  AND ccy.authVip=#{rank}


  AND (ccy.fullName LIKE CONCAT('%', #{keys}, '%') OR cu.tel LIKE CONCAT('%', #{keys}, '%'))
